import React, { useState, useEffect } from "react";
import GameCard from "./GameCard";

const RecommendedGames = ({ recommendedGames }) => {
  const [loading, setLoading] = useState(true);
  useEffect(() => {
    // Імітація завантаження на певий час (ваше Ajax запит)
    const timeout = setTimeout(() => {
      setLoading(false); // Припинення імітації завантаження
    }, 2000); // Приблизно 2 секунди. Потрібно замінити на ваш фактичний запит

    return () => clearTimeout(timeout);
  }, []);
  return (
    <div className="overflow-y-auto max-h-550">
      {loading ? (
        <div className="flex items-center justify-center h-full">
          {/* Ваш спінер або індикатор завантаження */}
          <div className="spinner"></div>{" "}
          {/* Замість цього може бути ваш спінер */}
          <p>Loading...</p> {/* Повідомлення про завантаження */}
        </div>
      ) : (
        recommendedGames.map(([gameName, steam_id], index) => (
          <GameCard key={index} gameName={gameName} steam_id={steam_id} />
        ))
      )}
    </div>
  );
};

export default RecommendedGames;
