import React, { useState } from "react";
import {
  Header,
  Footer,
  GameForm,
  RecommendedGames,
  Info,
} from "../components";

const Main = () => {
  const [recommendedGames, setRecommendedGames] = useState([]);
  return (
    <div>
      <Header />
      <div className="flex justify-center items-center">
        <Info />
        <GameForm setRecommendedGames={setRecommendedGames} />
        <RecommendedGames recommendedGames={recommendedGames} />
      </div>
      <Footer />
    </div>
  );
};

export default Main;
