import React, { useState } from "react";
import axios from "axios";

const GameForm = ({ onRecommendation }) => {
  const [gameIds, setGameIds] = useState([""]);

  const handleAddField = () => {
    const newGameIds = [...gameIds, ""];
    setGameIds(newGameIds);
  };

  const handleIdChange = (index, value) => {
    const newGameIds = [...gameIds];
    newGameIds[index] = value;
    setGameIds(newGameIds);
  };

  const handleRemoveField = (index) => {
    const newGameIds = [...gameIds];
    newGameIds.splice(index, 1);
    setGameIds(newGameIds);
  };

  const handleSubmit = (event) => {
    event.preventDefault();
    const ids = gameIds.filter((id) => id.trim() !== "");
    axios
      .post("/recommend", { game_ids: ids })
      .then((response) => {
        onRecommendation(response.data);
      })
      .catch((error) => {
        console.error("Error:", error);
      });
  };

  return (
    <div
      className="flex justify-center items-center"
      style={{ minHeight: "100vh" }}
    >
      <div
        className="p-4 bg-gray-100"
        style={{ width: "100%", maxWidth: "500px" }}
      >
        <form
          onSubmit={handleSubmit}
          className="border border-gray-300 rounded-lg p-4"
          style={{ minHeight: "600px", maxHeight: "80vh", overflowY: "auto" }}
        >
          {gameIds.map((id, index) => (
            <div key={index} className="flex items-center mb-2">
              <input
                type="text"
                value={id}
                onChange={(e) => handleIdChange(index, e.target.value)}
                placeholder="Введіть ID ігри"
                className="mr-2 px-4 py-2 border border-gray-300 rounded-lg focus:outline-none focus:border-blue-500"
              />
              <button
                type="button"
                onClick={() => handleRemoveField(index)}
                className="px-3 py-2 bg-red-500 text-white rounded-full hover:bg-red-600 transition duration-300 focus:outline-none"
              >
                -
              </button>
            </div>
          ))}
          <button
            type="button"
            onClick={handleAddField}
            className="px-3 py-2 bg-green-500 text-white rounded-full hover:bg-green-600 transition duration-300 focus:outline-none"
          >
            +
          </button>
          <button
            type="submit"
            className="ml-2 px-4 py-2 bg-blue-500 text-white rounded-lg hover:bg-blue-600 transition duration-300 focus:outline-none"
          >
            Отримати Рекомендації
          </button>
        </form>
      </div>
    </div>
  );
};

export default GameForm;
