import React from "react";

const GameCard = ({ gameName, steam_id, genres }) => {
  const imageUrl = `https://cdn.cloudflare.steamstatic.com/steam/apps/${steam_id}/hero_capsule.jpg`;

  return (
    <div className="flex items-center border border-gray-300 p-4 mb-4">
      {/* Placeholder for game image */}
      <div className="w-12 h-16 bg-gray-300 mr-4">
        <img src={imageUrl} alt="" className="w-12 h-16 mr-4" />
      </div>
      <div className="flex flex-col">
        <div className="font-bold mb-2">{gameName}</div>
        <div className="flex">
          {Array.isArray(genres) && genres.length > 0 ? (
            genres.map((genre, index) => (
              <div key={index} className="mr-2">
                {genre}
              </div>
            ))
          ) : (
            <div>No genres available</div>
          )}
        </div>
      </div>
    </div>
  );
};

export default GameCard;
