import React, { useState, useEffect } from "react";
import GameCard from "./GameCard";

const RecommendedGames = ({ recommendedGames, isLoading }) => {
  return (
    <div className="overflow-y-auto max-h-550">
      {isLoading ? (
        <div className="flex items-center justify-center h-full">
          {/* Ваш спінер або індикатор завантаження */}
          <div className="spinner"></div>{" "}
          {/* Замість цього може бути ваш спінер */}
          <p>Loading...</p> {/* Повідомлення про завантаження */}
        </div>
      ) : (
        recommendedGames.map((game, index) => (
          <GameCard
            key={index}
            gameName={game.title}
            steam_id={game.steam_gameId}
          />
        ))
      )}
    </div>
  );
};

export default RecommendedGames;
