import React, { useState, useEffect } from "react";
import GameCard from "./GameCard";
import axios from "axios";

const RecommendedGames = () => {
  const [recommendedGames, setRecommendedGames] = useState([]);

  useEffect(() => {
    // Припустимо, що ви викликаєте API для отримання рекомендацій
    axios
      .post("/recommend", { game_names: ["Game 1", "Game 2"] }) // Замініть це на ваші дані
      .then((response) => {
        setRecommendedGames(response.data.recommendation);
      })
      .catch((error) => {
        console.error("Error fetching recommendations:", error);
      });
  }, []);

  return (
    <div>
      {recommendedGames.map((gameName, index) => (
        <GameCard key={index} gameName={gameName} />
      ))}
    </div>
  );
};

export default RecommendedGames;
