import React, { useState } from "react";
import axios from "axios";

function GameForm({ onRecommendation }) {
  const [gameIds, setGameIds] = useState("");

  const handleSubmit = (event) => {
    event.preventDefault();
    const ids = gameIds.split(",").map((id) => id.trim());
    axios
      .post("/recommend", { game_ids: ids })
      .then((response) => {
        onRecommendation(response.data);
      })
      .catch((error) => {
        console.error("Error:", error);
      });
  };

  return (
    <div className="h-max">
      <form onSubmit={handleSubmit}>
        <input
          type="text"
          value={gameIds}
          onChange={(e) => setGameIds(e.target.value)}
          placeholder="Введіть ID ігор, розділені комою"
        />
        <button type="submit">Отримати Рекомендації</button>
      </form>
    </div>
  );
}

export default GameForm;
